CREATE VIEW `search_view` AS    select         `e`.`id` AS `id`,        `e`.`episode_number` AS `episode_number`,        `e`.`name` AS `episode_name`,        `s`.`name` AS `series_name`,        `g`.`name` AS `genre_name`,        `r`.`name` AS `rating_name`    from        (((`episode` `e`        join `series` `s` ON ((`e`.`series_id` = `s`.`id`)))        join `genre` `g` ON ((`e`.`genre_id` = `g`.`id`)))        join `rating` `r` ON ((`e`.`rating_id` = `r`.`id`)))